import React from 'react';

const Projects = () => {
  const projects = [
    {
      title: 'BookCourier â€“ Library-to-Home Delivery System',
      description: 'BookCourier is a modern, user-friendly library delivery management system designed to revolutionize how people access books. Our platform enables students, researchers, and general readers to request book pickups or deliveries easily.',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuChCkmg0U2iLzaIcnfMwcq-iuzR8n2F66FST1s9hbujSE1bsDBDIbnQmNbAPf8c-gh-_AQ8mLqlIFxiOSTHWgj673QTqyMC3lFtodwcFM3Upzm8On_4N0-_NiA0T5qElrQ1RLZAzJzIFSadlUOtJ4RIS_aR_8dozjrhqcW71jwNRupExoET4ZVfOobT-Es18UarKwg7er3uiyr7A1BkUQm2wua58RqV7KCk6RBBypmN9e2PoM4lUAl05SYYReHUrZdYhyJD9wX0aNI',
      technologies: ['HTML', 'Tailwind', 'Daisy UI', 'React', 'Firebase', 'MongoDB'],
      gradient: 'from-blue-500 to-purple-600'
    },
    {
      title: 'Daily Progress Habit_Tracker ðŸŽ¯',
      description: 'Habit_Tracker is an advanced habit tracking platform that helps users track daily progress, build streaks, and increase productivity. It offers secure Firebase Authentication, CRUD functionality for adding/editing habits, and progress visualization.',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuDeVKR0HQ8egX_SjTX4scFabZMv10U0IP1QfIa3_eLaASqPAZHBhC0HYu5DoIerKgHsUGw_nMMdoWCKpi8ljsWyje4-blUCBP6oxKXK58k0dUxnZVNEc8WehG2fdvLbDLe9HsAEwxCjIPbKnMAT23vtTx2A_8dtk7kOft9AnrvcD4-J4uQ-RoI7yxiBFl4wFfZjhYnkcLuk8hEqUm3wcfrZlzaLVXN-Q3IleMLW7HKVsPOjA3igyrQGXS5H9vRsNpT3Kc3U6Gp7Qfg',
      technologies: ['React', 'Firebase', 'Tailwind', 'Express JS', 'MongoDB'],
      gradient: 'from-green-500 to-teal-600'
    },
    {
      title: 'ToyLand ðŸ§¸ Project with Firebase Auth',
      description: 'ToyLand - A feature-rich platform centered on Firebase Authentication (Sign-in, Sign-up, Google Sign-in). Users can browse the Toy Blog, view detailed Toy Cards, and manage their account on the My Profile page.',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuBjmqxAykMAzq3vP2tMKEsw5nljw0h3yveIszf3q1b9DSHM6cbMVER6V8qRXq6Ifm5WFCf7x_r2V4UiiecKHt-PPHLqDPPyd6CHG1AxiZA-LAzo_nUwwLzSvpsYAQmVpshvj4Dc9_uu14qkvmoWc5Qa5X7LIUTjjKajrIeEy-sEo6DIN20MWaOuU2r_-YxFyiXna41pHA1SN9W0jTpQJUZDUyDZ8_NRHdsThcIJHHRVRAWTgZw1DWkMP5E7bXi2csEBIaV5HmfjdQM',
      technologies: ['React', 'Firebase', 'Tailwind', 'Daisy UI'],
      gradient: 'from-pink-500 to-rose-600'
    },
    {
      title: 'TreeStore ðŸŒ³ Project with API',
      description: 'TreeStore - an API-powered eCommerce platform for exploring plant collections with seamless add-to-cart functionality and a clean, responsive design.',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuASo7x8Ix5boiYvYVceaUkfZ4zD2A544DmvarUA5WlN0O798KGwJ3g2uaorAz3xlijv9-oMPsplFBWEC-159wqLO9VWT423fDJoj-GQXLrmhRh8Buq-sxtz9UiOCtEhJQqcYpMZFg6BpDfR-h2u78iUM5Tuhp_FHcO_Xj69O4-LG4HbiNYyb8D5dhyTlV2ipIDWBZXCqgzPOwOclmHie_K_GkvCY_NmyR1qgoiit1LBzFrtG-B2qowVMELClmO77iVIuRQqB-gWOTo',
      technologies: ['HTML', 'Tailwind', 'JS'],
      gradient: 'from-emerald-500 to-green-600'
    },
    {
      title: 'AppStore ðŸ“±: API, Install, Uninstall',
      description: 'AppStore ðŸ“±: API, Install, Uninstall is an API-powered private platform. Here, visitors can easily search, install, and uninstall apps. Installed apps are saved in a dedicated "Installation" option.',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuBdPcGbpuMKO034uLkLupoYjkQTcrxnO9woz0CKTjmR3WVI4_eyflijnU9MRU3n_qw7kAhfbXkg-mcAU0b3s11wEmVWfC7DLcnMOMAeizQU_Y1NT8MTakI-zsw9Gemu3x747BxS8VH3VqTQcmraeYXjNu1PvMhoBN5PERiEa4mD3VEfP6YcMeJEhoImIPONGRePflOm3Uk_nUy6cWOjU2C_R9etnQiRMDvcwKn6oQXhhzWFLItKA9j3xQI6M1vxtSHfC13xCwPNUzI',
      technologies: ['HTML', 'Tailwind', 'React'],
      gradient: 'from-indigo-500 to-purple-600'
    },
    {
      title: 'SupportHub',
      description: 'SupportHub is a customer support or helpdesk system. Its main function is to organize and track all the problems or requests (called Tickets) of customers or users in a central place.',
      image: 'https://lh3.googleusercontent.com/aida-public/AB6AXuBzJ-AC8nBJIySB488za_uu_vLloOhBQAQnrq4-kJaUwgV6oFPHddSeThOMrDWCNSIrNzsW5a0wWKsX2iNt7kNEqtCoBwqNLwtvRgoqbzcgHXUvvlZ9mwF7jedy772jw3sFhusr1LkEc80PoAizrdHL1UXTrnqg0gWU28JrmgmEcELvqNUPe9JBAtUeLb1QLn86z3XN_0Wwvnpqxzjoy12E8xnJS5tj_DOesmgfvaYC1Ki-3rQxXeqAIjfZZcWdHycVqgyCf2vew8M',
      technologies: ['HTML', 'CSS', 'Tailwind', 'React'],
      gradient: 'from-orange-500 to-red-600'
    }
  ];

  const ProjectCard = ({ project, index }) => (
    <article className="bg-white dark:bg-card-dark rounded-2xl overflow-hidden border border-gray-200 dark:border-white/10 hover:border-primary/50 transition-all duration-300 group flex flex-col h-full shadow-lg hover:shadow-2xl transform hover:scale-105">
      {/* Image Container */}
      <div className="h-48 overflow-hidden relative">
        <img 
          alt={`${project.title} Screenshot`}
          className="w-full h-full object-cover transform group-hover:scale-110 transition-transform duration-500" 
          src={project.image}
        />
        <div className={`absolute inset-0 bg-gradient-to-t ${project.gradient} opacity-20 group-hover:opacity-30 transition-opacity duration-300`}></div>
        <div className="absolute inset-0 bg-gradient-to-t from-black/80 via-transparent to-transparent"></div>
        
        {/* Floating Index */}
        <div className="absolute top-4 left-4 w-8 h-8 bg-primary rounded-full flex items-center justify-center text-white font-bold text-sm">
          {index + 1}
        </div>
      </div>
      
      {/* Content */}
      <div className="p-6 flex-grow flex flex-col">
        <h3 className="text-xl font-display font-bold text-gray-800 dark:text-white mb-3 group-hover:text-primary transition-colors line-clamp-2">
          {project.title}
        </h3>
        
        <p className="text-gray-600 dark:text-gray-400 text-sm mb-6 flex-grow leading-relaxed">
          {project.description}
        </p>
        
        {/* Technologies */}
        <div className="flex flex-wrap gap-2 mb-6">
          {project.technologies.map((tech, techIndex) => (
            <span 
              key={techIndex}
              className="px-3 py-1 text-xs rounded-full bg-gradient-to-r from-primary/10 to-secondary/10 text-primary border border-primary/20 hover:border-primary/50 transition-colors font-medium"
            >
              {tech}
            </span>
          ))}
        </div>
        
        {/* Action Buttons */}
        <div className="mt-auto flex gap-4">
          <button className="flex-1 text-sm font-bold text-gray-600 dark:text-gray-300 hover:text-primary transition-colors flex items-center justify-center gap-2 py-2 px-4 border border-gray-300 dark:border-gray-600 rounded-lg hover:border-primary/50 hover:bg-primary/5">
            <i className="fab fa-github"></i> 
            Code
          </button>
          <button className="flex-1 text-sm font-bold text-white bg-gradient-to-r from-primary to-secondary hover:from-primary/80 hover:to-secondary/80 transition-all flex items-center justify-center gap-2 py-2 px-4 rounded-lg transform hover:scale-105">
            <i className="fas fa-external-link-alt"></i> 
            Live Demo
          </button>
        </div>
      </div>
    </article>
  );

  return (
    <section className="py-20 px-4 bg-gradient-to-br from-white via-blue-50 to-purple-50 dark:from-background-dark dark:via-gray-900 dark:to-purple-900/10" id="projects">
      <div className="max-w-7xl mx-auto">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-display font-bold mb-4">
            <span className="text-primary neon-text">Featured</span>{' '}
            <span className="text-secondary neon-text-pink">Projects</span>
          </h2>
          <div className="h-1 w-32 bg-gradient-to-r from-primary to-secondary mx-auto rounded-full"></div>
          <p className="text-gray-500 dark:text-gray-400 mt-4 text-lg max-w-2xl mx-auto">
            A showcase of my recent work and projects that demonstrate my skills in full-stack development
          </p>
        </div>
        
        {/* Projects Grid */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {projects.map((project, index) => (
            <ProjectCard key={index} project={project} index={index} />
          ))}
        </div>
        
        {/* Call to Action */}
        <div className="text-center mt-16">
          <div className="bg-white/80 dark:bg-card-dark/80 backdrop-blur-sm p-8 rounded-2xl border border-gray-200 dark:border-white/10 shadow-lg inline-block">
            <h3 className="text-2xl font-display font-bold mb-4 text-gray-800 dark:text-white">
              Want to see more?
            </h3>
            <p className="text-gray-600 dark:text-gray-400 mb-6">
              Check out my GitHub for more projects and contributions
            </p>
            <button className="px-8 py-3 bg-gradient-to-r from-primary to-secondary text-white font-bold rounded-full hover:scale-105 transform transition-all duration-300 shadow-lg hover:shadow-primary/50">
              <i className="fab fa-github mr-2"></i>
              View All Projects
            </button>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
