import React from 'react';
import { motion } from 'framer-motion';

const Header = ({ darkMode, toggleDarkMode }) => {
  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <nav className="fixed w-full z-50 top-0 left-0 bg-white/80 dark:bg-background-dark/80 backdrop-blur-md border-b border-gray-200 dark:border-white/5 px-6 py-4 transition-all duration-300">
      <div className="max-w-7xl mx-auto flex justify-between items-center">
        <div className="font-display font-bold text-2xl text-primary neon-text tracking-wider cursor-pointer hover:scale-105 transition-transform">
          RIFAT
        </div>
        
        <div className="hidden md:flex space-x-8 text-sm font-medium">
          <button 
            onClick={() => scrollToSection('about')}
            className="hover:text-primary transition-colors hover:scale-105 transform duration-200"
          >
            About
          </button>
          <button 
            onClick={() => scrollToSection('skills')}
            className="hover:text-primary transition-colors hover:scale-105 transform duration-200"
          >
            Skills
          </button>
          <button 
            onClick={() => scrollToSection('projects')}
            className="hover:text-primary transition-colors hover:scale-105 transform duration-200"
          >
            Projects
          </button>
          <button 
            onClick={() => scrollToSection('contact')}
            className="hover:text-primary transition-colors hover:scale-105 transform duration-200"
          >
            Contact
          </button>
        </div>
        
        <button 
          onClick={toggleDarkMode}
          className="p-2 rounded-full bg-gray-200 dark:bg-gray-800 text-gray-800 dark:text-gray-200 hover:bg-primary hover:text-white transition-all duration-300 hover:scale-110 transform"
        >
          <i className={`fas ${darkMode ? 'fa-sun' : 'fa-moon'}`}></i>
        </button>
      </div>
    </nav>
  );
};

export default Header;
